import logo from './Ping Pong Table.jpg';
import './App.css';
import { Player } from './Player';
import { updateScore } from './ScoreHandling';

function App() {
  const name1 = 'Player1'; //prompt("Welcome! Enter First Player Name.", "Name");
  const p1 = new Player(name1);
  p1.isPocket = true;

  const name2 = 'Player2'; //prompt("Welcome! Enter First Player Name.", "Name");
  const p2 = new Player(name2);
  p2.position = 2;

  const name3 = 'Player3';//prompt("Welcome! Enter First Player Name.", "Name");
  const p3 = new Player(name3);
  p3.position = 3;
  console.log(p1);
  console.log(p2);
  console.log(p3);


  return (
    <div className="App">
      <header className="App-header">
        <div style={{position: 'fixed', top: '13%', width: '100%'}}>
          <div id="Pocket Player" className="Player-score" >00</div>
          <button className="Paddle-button" id="PlayerPocket" onClick={p1.incrementScore} draggable="true">
            J
          </button>
        </div>
        <div id="Scoring Players" style={{position: 'fixed', top: '78%', display: 'flex', justifyContent: 'space-between', width: '70%'}}>
          <div id="Left Player" className="Player-score">00</div>
          <button className="Paddle-button" id="PlayerLeft" onClick={p2.incrementScore}>
            R
          </button>
          <div id="Right Player" className="Player-score">00</div>
          <button className="Paddle-button" id="PlayerRight" onClick={p3.incrementScore}>
            D
          </button>
        </div>
      </header>
    </div>
  );
}

export default App;
