    <div className="App">
      <header className="App-header">
        <div id="Pocket Player" style={{position: 'fixed', top: '13%', width: '100%'}}>
          <div id="Player1" className="Player-score" >00</div>
          <button className="Paddle-button" onClick={console.log('Clicked!')}>
            J
          </button>
        </div>
        <div id="Scoring Players" style={{position: 'fixed', top: '78%', display: 'flex', justifyContent: 'space-between', width: '70%'}}>
          <div className="Player-score">00</div>
