import './App.css';
import { Player } from './Player';
import { updateScore } from './ScoreHandling';
import paddleLogo from './paddle.png';

function App() {
  const name1 = 'Player1'; //prompt("Welcome! Enter First Player Name.", "Name");
  const p1 = new Player(name1);
  p1.isPocket = true;

  const name2 = 'Player2'; //prompt("Welcome! Enter First Player Name.", "Name");
  const p2 = new Player(name2);
  p2.position = 2;

  const name3 = 'Player3';//prompt("Welcome! Enter First Player Name.", "Name");
  const p3 = new Player(name3);
  p3.position = 3;
  console.log(p1);
  console.log(p2);
  console.log(p3);


  return (
    <div className="App">
      <header>
        <h1>Pocket Pong</h1>
      </header>
      <div id='pocket row'>
        <div id="pocketPlayer" className="paddleContainer">
          <img src={paddleLogo} width='150' height='150'/>
          <div className='playerScore'>0</div>
          <div className ='PlayerName'>Jacob</div>
        </div>
      </div>
      <div id='lower row' style={{float: 'left', position: 'relative'}}>
        <div id="leftPlayer" className="paddleContainer" style={{float: 'left'}}>
          <img src={paddleLogo} width='150' height='150'/>
          <div className='playerScore'>0</div>
          <div className ='PlayerName'>Jacob</div>
        </div>
        <div id="rightPlayer" className="paddleContainer" style={{float: 'left'}}>
          <img src={paddleLogo} width='150' height='150'/>
          <div className='playerScore'>0</div>
          <div className ='PlayerName'>Jacob</div>
        </div>
      </div>
    </div>
  );
}

export default App;
