import { Player } from "./Player";

const updateScore = (player) => {

    player.incrementScore();
};

const p1 = new Player('Jacob');
const p2 = new Player('Raymond');
const p3 = new Player('David');

if(document.getElementById('Player1') != null) {
    document.getElementById('Player1').onclick = updateScore(p1);
}

export {updateScore};
