import { Player } from "./Player";
import { playerRoster } from "./playerRoster";

const dragAndReorder = (elementName, event) => {

    let draggedButton = document.getElementById(elementName);
    console.log(event);

    let droppableArea = document.getElementById('upper half');
    let upperHalfCoordinate = droppableArea.getBoundingClientRect();
    console.log(upperHalfCoordinate);
        
    console.log(`Upper half defined as ${upperHalfCoordinate} and element dropped at ${event.clientY}`);
};

export {dragAndReorder};
