export class Player {

    constructor(name) {
        this._name = name;
        this._score = 0;
        this._position = 1;
        this._isPocket = false;
    }

    get name() {
        return this._name;
    }

    get score() {
        return this._score;
    }

    get position() {
        return this._position;
    }

    get isPocket() {
        return this.isPocket;
    }

    set score(newScore) {
        this._score = newScore;
    }

    set position(newPosition) {
        this._position = newPosition;
    }

    set isPocket(pos) {
        this._isPocket = pos;
    }

    incrementScore = () => {
        this._score++;
        console.log(this._score);

        if(this._position === 1) {
            document.getElementById('pocketPlayer').innerHTML = this._score;
        }
        else if(this._position === 2) {
            document.getElementById('leftPlayer').innerHTML = this._score;
        }
        else {
            document.getElementById('rightPlayer').innerHTML = this._score;
        }
    }
}
